import "./Pagers.css";

import Panel from "components/Panel/Panel";
import Button from "components/Button/Button";

const Pagers = ({ idx, total, ...pageProps }) => {
  if (!idx) {
    idx = 1;
  }

  const handlePageSelect = (pageIdx) => {
    console.log(pageIdx);
    let domPage1 = document.querySelector("#Button" + idx);
    domPage1.classList.remove("Default-button-active");
    let domPage2 = document.querySelector("#Button" + pageIdx);
    domPage2.classList.add("Default-button-active");
    idx = pageIdx;
  };

  const handlePageClick = (pageIdx) => {
    console.log(pageIdx);
    handlePageSelect(pageIdx);
  };

  const handlePrev = () => {
    let prevIdx = total;
    if (idx != 1) {
      prevIdx = Math.abs((idx - 1) % total);
    }
    console.log(prevIdx);
    handlePageSelect(prevIdx);
  };

  const handleNext = () => {
    let nextIdx = (idx + 1) % total;
    console.log(nextIdx);
    handlePageSelect(nextIdx);
  };

  return (
    <Panel>
    <Button
      id={"ButtonPrev"}
      onClick={() => handlePrev()}
      text="Prev"
    />
    {[...Array(total)].map((x, i) =>
      (idx == i + 1) ?
        <Button
          id={"Button" + (i + 1)}
          active = {true}
          onClick={() => handlePageClick({pageIdx: i+1})}
          text={i+1}
        />
      :
        <Button
          id={"Button" + (i + 1)}
          onClick={() => handlePageClick({pageIdx: i+1})}
          text={i+1}
        />

    )}
    <Button
      id={"ButtonNext"}
      onClick={() => handleNext()}
      text="Next"
    />
    </Panel>
  );
};

export default Pagers;
