import "./Pagers.css";

import Panel from "components/Panel/Panel";
import Button from "components/Button/Button";

const Pagers = ({ idx, total, ...pageProps }) => {
  if (!idx) {
    idx = 1;
  }
  const handlePageClick = (pageIdx) => {
    console.log(pageIdx);
  };

  const handlePrev = () => {
    let prevIdx = total;
    if (idx != 1) {
      prevIdx = Math.abs((idx - 1) % total);
    }
    console.log(prevIdx);
    idx = prevIdx;
  };

  const handleNext = () => {
    let nextIdx = (idx + 1) % total;
    console.log(nextIdx);
    idx = nextIdx;
  };

  return (
    <Panel>
    <Button
      onClick={() => handlePrev()}
      text="Prev"
    />
    {[...Array(total)].map((x, i) =>
      (idx == i + 1) ?
        <Button
          active = {true}
          onClick={() => handlePageClick({pageIdx: i+1})}
          text={i+1}
        />
      :
        <Button
          onClick={() => handlePageClick({pageIdx: i+1})}
          text={i+1}
        />

    )}
    <Button
      onClick={() => handleNext()}
      text="Next"
    />
    </Panel>
  );
};

export default Pagers;
