import "./Pagers.css";

import Panel from "components/Panel/Panel";
import Button from "components/Button/Button";

const Pagers = ({ idx, total, ...pageProps }) => {
  if (!idx) {
    idx = 1;
  }
  const handleClick = (idx) => {
    console.log(total);
    console.log(idx);
  };

  return (
    <Panel>
    <Button
      onClick={() => handleClick({idx})}
      text="Prev"
    />
    {[...Array(total)].map((x, i) =>
      (idx == i + 1) ?
        <Button
          active = {true}
          onClick={() => handleClick({idx: i+1})}
          text={i+1}
        />
      :
        <Button
          onClick={() => handleClick({idx: i+1})}
          text={i+1}
        />

    )}
    <Button
      onClick={() => handleClick({idx})}
      text="Next"
    />
    </Panel>
  );
};

export default Pagers;
